import { useTheme } from '@/components/ThemeProvider';

type Props = {
  variant?: 'default' | 'icon';
  size?: number;
  color?: string;
};

export const Logo = ({ variant = 'default' }: Props) => {
  const { theme } = useTheme();

  // Sprawdź czy motyw to light (bezpośrednio lub przez system)
  const isLight =
    theme === 'light' ||
    (theme === 'system' &&
      !window.matchMedia('(prefers-color-scheme: dark)').matches);

  const lightLogoIcon =
    'data:image/png;base64,';
  const darkLogoIcon =
    'data:image/png;base64,';

  const lightLogoBig =
    'data:image/png;base64,';

  const darkLogoBig =
    'data:image/png;base64,';

  if (variant === 'default') {
    return (
      <svg
        xmlns='http://www.w3.org/2000/svg'
        viewBox={`0 0 150 50`}
        width='150'
        height='50'
      >
        <defs>
          <image
            width='150'
            height='50'
            id='img1'
            href={isLight ? darkLogoBig : lightLogoBig}
          />
        </defs>
        <style></style>
        <use
          id='Background'
          href='#img1'
          x='1'
          y='0'
        />
      </svg>
    );
  } else {
    return (
      <svg
        width='40'
        height='40'
        viewBox={`0 0 74 48`}
        fill='none'
        xmlns='http://www.w3.org/2000/svg'
      >
        <defs>
          <image
            width='40'
            height='40'
            id='img1'
            href={isLight ? darkLogoIcon : lightLogoIcon}
          />
        </defs>
        <style></style>
        <use
          id='Background'
          href='#img1'
          x='7'
          y='2'
        />
      </svg>
    );
  }
};
